            <tr>
                <td>{{ client.id }}</td>
                <td>{{ client.nomClient }}</td>
                <td>{{ client.prenomClient }}</td>
                <td>{{ client.telephoneClient }}</td>
                <td>{{ client.sommeVerce }}</td>
                <td>{{ client.dette }}</td>
                <td>
                    <a href=\"{{ path('client_show', {'id': client.id}) }}\"
